import React, { useEffect, useState } from 'react';
import axios from 'axios';


const Slot = () => {
  const [districts, setDistricts] = useState([]);
  const [selectedDistrict, setSelectedDistrict] = useState('');
  const [slots, setSlots] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // Fetch the list of districts in Delhi
    axios
      .get('https://cdn-api.co-vin.in/api/v2/admin/location/districts/16')
      .then(response => {
        setDistricts(response.data.districts);
        setSelectedDistrict(response.data.districts[0].district_id);
      })
      .catch(error => {
        console.error('Error fetching districts:', error);
      });
  }, []);

  const handleDistrictChange = event => {
    setSelectedDistrict(event.target.value);
  };

  const handleSearch = () => {
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);
    const formattedDate = `${tomorrow.getDate()}-${tomorrow.getMonth() + 1}-${tomorrow.getFullYear()}`;

    setLoading(true);

    // Fetch the available vaccine slots for the selected district and tomorrow's date
    axios
      .get(`https://cdn-api.co-vin.in/api/v2/appointment/sessions/public/findByDistrict?district_id=${selectedDistrict}&date=${formattedDate}`)
      .then(response => {
        setSlots(response.data.sessions);
      })
      .catch(error => {
        console.error('Error fetching slots:', error);
      })
      .finally(() => {
        setLoading(false);
      });
  };

  return (
    <div className="container">
      <h1>COVID-19 Vaccine Slot Finder</h1>
      <div>
        <label htmlFor="district">Select District:</label>
        <select id="district" value={selectedDistrict} onChange={handleDistrictChange}>
          {districts.map(district => (
            <option key={district.district_id} value={district.district_id}>
              {district.district_name}
            </option>
          ))}
        </select>
      </div>
      <button onClick={handleSearch} disabled={loading}>
        {loading ? 'Searching...' : 'Search Slots'}
      </button>
      <div>
        <h2>Vaccine Slots for Tomorrow</h2>
        {slots.length === 0 ? (
          <p>No slots available.</p>
        ) : (
          <ul>
            {slots.map(slot => (
              <li key={slot.session_id}>
                {slot.name} - {slot.address}, {slot.pincode}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};


export default Slot;
